import React, { useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Question() {
  const dispatch = useContext(Dispatch);
  const globalData = useContext(Data);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await fetch("/api");
      const questions = await response.json();
      const shitFind = questions.data.shitFind;
      const randomQuestion = Math.floor(Math.random() * shitFind.length);
      dispatch({
        type: "QUESTION_INSERT",
        question: shitFind[randomQuestion].question,
        answer: shitFind[randomQuestion].answer,
      });
    };
    fetchQuestions();
  }, []);

  return (
    <div className="App__container--question">
      <span className="App__container--question--bf">-</span>
      {globalData.question}
    </div>
  );
}

export default Question;
