import React, { useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Question() {
  const dispatch = useContext(Dispatch);
  const globalData = useContext(Data);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await fetch("/api");
      const resData = await response.json();
      const questionsData = resData.questionsFind;
      const possibleAnswersData =
        resData.possibleAnswersFind[0].possibleAnswers;
      const randomQuestion = Math.floor(Math.random() * questionsData.length);

      dispatch({
        type: "QUESTION_ANSWER_INSERT",
        question: questionsData[randomQuestion].question,
        answer: questionsData[randomQuestion].answer,
        possibleAnswers: possibleAnswersData,
      });
    };
    fetchQuestions();
  }, []);

  return (
    <div className="App__container--question">
      <span className="App__container--question--bf">-</span>
      {globalData.question}
    </div>
  );
}

export default Question;
