import React, { useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Answers() {
  const dispatch = useContext(Dispatch);
  const globalData = useContext(Data);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch("/answers");
      const data = await response.json();
      const arr = [];
      while (arr.length < 4) {
        let n = Math.floor(Math.random() * 4);
        if (arr.indexOf(n) === -1) arr.push(n);
      }
      console.log(arr);

      dispatch({
        type: "DEFINE_ANSWER",
        payload: [
          data.person[arr[0]],
          data.person[arr[1]],
          data.person[arr[2]],
          data.person[arr[3]],
        ],
      });
    };
    fetchData();
  }, []);

  function answerClicked() {
    console.log(globalData);
  }

  return (
    <ul className="App__container--list">
      {globalData.answer.map((ans, index) => (
        <li
          key={index}
          onClick={answerClicked}
          className="App__container--list--answer"
        >
          <span>{ans}</span>
          <img
            alt=""
            className="App__container--list--chevron"
            src="./chevron.png"
          />
        </li>
      ))}
    </ul>
  );
}

export default Answers;
