import React, { useEffect, useContext } from "react";
import { Data, Dispatch } from "./Context";

function Answers() {
  const dispatch = useContext(Dispatch);
  const globalData = useContext(Data);
  const { possibleAnswers, dataFetched, answer } = globalData;

  useEffect(() => {
    if (dataFetched) {
      const arr = [];
      while (arr.length < 3) {
        let n = Math.floor(Math.random() * 3);
        if (arr.indexOf(n) === -1) arr.push(n);
      }

      dispatch({
        type: "POSSIBLE_ANSWERS_FILTER",
        payload: [
          possibleAnswers[arr[0]],
          possibleAnswers[arr[1]],
          possibleAnswers[arr[2]],
        ],
      });
    }
  }, [dataFetched]);

  function answerClicked(i) {
    console.log(i);
    console.log("clicked");
  }

  return (
    <ul className="App__container--list">
      {globalData.answerSorted.map((ans, index) => (
        <li
          key={index}
          onClick={() => answerClicked(index)}
          className="App__container--list--answer"
        >
          <span>{ans}</span>
          <img
            alt=""
            className="App__container--list--chevron"
            src="./chevron.png"
          />
        </li>
      ))}
    </ul>
  );
}

export default Answers;
